export const initialState ={
    foodItems:[],
    status:"idle",
    filter:"Home"
}
export const foodSlice = createSlice({
    name:"food",
    initialState : initialState,
    reducers:{
        check:(state)=>{
            console.log("temprary");
            console.log(state.status)
            if(state.status==="idle")
                state.status = "waiting"


        },
